INSERT INTO REGISTRATION(RESERV_NO ,KIND_NO ,PET_NO ,USER_NO ,VISIT_DATE ,PET_INTRO ,PET_NOTE ,ORIGIN_NAME ,CHANGE_NAME ,APPROVAL) VALUES (SEQ_REG.NEXTVAL ,#{kindNo} ,#{petNo} ,#{userNo} ,#{visitDate} ,#{petIntro} ,#{petNote} ,#{originName} ,#{changeName} ,DEFAULT )
SELECT RESERV_NO reservNo ,R.KIND_NO kindNo ,K.KIND_NAME kindName ,R.PET_NO petNo ,PET_NAME petName ,BREED_NAME breed ,R.USER_NO userNo ,NAME userName ,VISIT_DATE visitDate ,PET_INTRO petIntro ,PET_NOTE petNote ,ORIGIN_NAME originName ,CHANGE_NAME changeName ,APPROVAL approval FROM REGISTRATION R, PET P, MEMBER M, KINDERGARTEN K, BREED B WHERE R.USER_NO = M.USER_NO AND K.KIND_NO = R.KIND_NO AND R.PET_NO = P.PET_NO AND P.BREED_ID = B.BREED_ID AND P.OWNER_NO = M.USER_NO AND R.USER_NO = #{userNo}
SELECT RESERV_NO reservNo ,R.KIND_NO kindNo ,K.KIND_NAME kindName ,R.PET_NO petNo ,PET_NAME petName ,BREED_NAME breed ,R.USER_NO userNo ,NAME userName ,VISIT_DATE visitDate ,PET_INTRO petIntro ,PET_NOTE petNote ,ORIGIN_NAME originName ,CHANGE_NAME changeName ,APPROVAL approval FROM REGISTRATION R, PET P, MEMBER M, KINDERGARTEN K, BREED B WHERE R.USER_NO = M.USER_NO AND K.KIND_NO = R.KIND_NO AND R.PET_NO = P.PET_NO AND P.BREED_ID = B.BREED_ID AND P.OWNER_NO = M.USER_NO AND K.DIRECTOR_ID = #{userNo}
SELECT RESERV_NO reservNo ,R.KIND_NO kindNo ,K.KIND_NAME kindName ,R.PET_NO petNo ,PET_NAME petName ,BREED_NAME breed ,R.USER_NO userNo ,NAME userName ,VISIT_DATE visitDate ,PET_INTRO petIntro ,PET_NOTE petNote ,ORIGIN_NAME originName ,CHANGE_NAME changeName ,APPROVAL approval FROM REGISTRATION R, PET P, MEMBER M, KINDERGARTEN K, BREED B WHERE R.USER_NO = M.USER_NO AND K.KIND_NO = R.KIND_NO AND R.PET_NO = P.PET_NO AND P.BREED_ID = B.BREED_ID AND P.OWNER_NO = M.USER_NO AND R.RESERV_NO = #{reservNo}
DELETE FROM REGISTRATION WHERE RESERV_NO = #{reservNo}
UPDATE REGISTRATION SET
PET_INTRO = #{petIntro}, PET_NOTE = #{petNote}, ORIGIN_NAME = #{originName}, CHANGE_NAME = #{changeName} WHERE RESERV_NO = #{reservNo}
